01012                                                            {
01013   // check that PT is not incomplete/dependent
01014   CXCursor PC = clang_getTypeDeclaration(PT);
01015   long long Error = validateFieldParentType(PC,PT);
01016   if (Error < 0)
01017     return Error;
01018   if (!S)
01019     return CXTypeLayoutError_InvalidFieldName;
01020   // lookup field
01021   ASTContext &Ctx = cxtu::getASTUnit(GetTU(PT))->getASTContext();
01022   IdentifierInfo *II = &Ctx.Idents.get(S);
01023   DeclarationName FieldName(II);
01024   const RecordDecl *RD =
01025         dyn_cast_or_null<RecordDecl>(cxcursor::getCursorDecl(PC));
01026   // verified in validateFieldParentType
01027   RD = RD->getDefinition();
01028   RecordDecl::lookup_result Res = RD->lookup(FieldName);
01029   // If a field of the parent record is incomplete, lookup will fail.
01030   // and we would return InvalidFieldName instead of Incomplete.
01031   // But this erroneous results does protects again a hidden assertion failure
01032   // in the RecordLayoutBuilder
01033   if (!Res.isSingleResult())
01034     return CXTypeLayoutError_InvalidFieldName;
01035   if (const FieldDecl *FD = dyn_cast<FieldDecl>(Res.front()))
01036     return Ctx.getFieldOffset(FD);
01037   if (const IndirectFieldDecl *IFD = dyn_cast<IndirectFieldDecl>(Res.front()))
01038     return Ctx.getFieldOffset(IFD);
01039   // we don't want any other Decl Type.
01040   return CXTypeLayoutError_InvalidFieldName;
01041 }

00931                                          {
00932   if (T.kind == CXType_Invalid)
00933     return CXTypeLayoutError_Invalid;
00934   ASTContext &Ctx = cxtu::getASTUnit(GetTU(T))->getASTContext();
00935   QualType QT = GetQualType(T);
00936   // [expr.sizeof] p2: if reference type, return size of referenced type
00937   if (QT->isReferenceType())
00938     QT = QT.getNonReferenceType();
00939   // [expr.sizeof] p1: return -1 on: func, incomplete, bitfield, incomplete
00940   //                   enumeration
00941   // Note: We get the cxtype, not the cxcursor, so we can't call
00942   //       FieldDecl->isBitField()
00943   // [expr.sizeof] p3: pointer ok, function not ok.
00944   // [gcc extension] lib/AST/ExprConstant.cpp:1372 HandleSizeof : vla == error
00945   if (QT->isIncompleteType())
00946     return CXTypeLayoutError_Incomplete;
00947   if (QT->isDependentType())
00948     return CXTypeLayoutError_Dependent;
00949   if (!QT->isConstantSizeType())
00950     return CXTypeLayoutError_NotConstantSize;
00951   if (const auto *Deduced = dyn_cast<DeducedType>(QT))
00952     if (Deduced->getDeducedType().isNull())
00953       return CXTypeLayoutError_Undeduced;
00954   // [gcc extension] lib/AST/ExprConstant.cpp:1372
00955   //                 HandleSizeof : {voidtype,functype} == 1
00956   // not handled by ASTContext.cpp:1313 getTypeInfoImpl
00957   if (QT->isVoidType() || QT->isFunctionType())
00958     return 1;
00959   return Ctx.getTypeSizeInChars(QT).getQuantity();
00960 }

