01153   if (!TA)
01154     return MakeCXType(QualType(), GetTU(CT));
01155 
01156   Optional<QualType> QT = FindTemplateArgumentTypeAt(TA.getValue(), index);
01157   return MakeCXType(QT.getValueOr(QualType()), GetTU(CT));
01158 }

