01230                                                          {
01231   CXCursor PC = clang_getTypeDeclaration(PT);
01232   if (clang_isInvalid(PC.kind))
01233     return false;
01234   const RecordDecl *RD =
01235         dyn_cast_or_null<RecordDecl>(cxcursor::getCursorDecl(PC));
01236   if (!RD || RD->isInvalidDecl())
01237     return false;
01238   RD = RD->getDefinition();
01239   if (!RD || RD->isInvalidDecl())
01240     return false;
01241 
01242   for (RecordDecl::field_iterator I = RD->field_begin(), E = RD->field_end();
01243        I != E; ++I){
01244     const FieldDecl *FD = dyn_cast_or_null<FieldDecl>((*I));
01245     // Callback to the client.
01246     switch (visitor(cxcursor::MakeCXCursor(FD, GetTU(PT)), client_data)){
01247     case CXVisit_Break:
01248       return true;
01249     case CXVisit_Continue:
01250       break;
01251     }
01252   }
01253   return true;
