00011     // get the current token at the specified location
00012     Optional <Token> currentToken( clang::Lexer::findNextToken(
00013             sourceLocation, localContext->getSourceManager( ),
00014             localContext->getLangOpts( )));
00015 
00016     // if token is raw identifier return last token
00017     if ( currentToken->getKind( ) == clang::tok::raw_identifier ) {
00018         sourceLocation = sourceLocation.getLocWithOffset( -1 );
00019     }
00020     // if token is semi return this token
00021     else if ( currentToken->getKind( ) == clang::tok::semi ) {
00022         sourceLocation = sourceLocation.getLocWithOffset( 0 );
00023     }
00024     // if token is brace return next token
00025     else if ( currentToken->getKind( ) == clang::tok::r_brace ) {
00026         sourceLocation = sourceLocation.getLocWithOffset( 1 );
00027     }
00028 
00029     // find location after semicolon
00030     SourceLocation locAfterSemi( clang::Lexer::findLocationAfterToken(
00031             sourceLocation, clang::tok::semi,
00032             localContext->getSourceManager( ),
00033             localContext->getLangOpts( ), true ));
00034 
00035     // find location after brace
00036     SourceLocation locAfterBrace( clang::Lexer::findLocationAfterToken(
00037             sourceLocation, clang::tok::r_brace,
00038             localContext->getSourceManager( ),
00039             localContext->getLangOpts( ), true ));
00040 
00041     // if loc after semi is valid return locAfterSemi
00042     if ( currentToken->getKind( ) != clang::tok::r_brace && locAfterSemi.isValid( )) {
00043         sourceLocation = locAfterSemi;
00044     }
00045     // if loc after brace is valid return locAfterBrace
00046     else if ( currentToken->getKind( ) != clang::tok::r_brace && locAfterBrace.isValid( )) {
00047         sourceLocation = locAfterBrace;
00048     }
00049 
00050     // if token is brace return current location
00051 
00052     return sourceLocation;
