00060 void traverseChildren( Stmt *funcBody, ASTContext *localContext ) {
00061 
00062     // count statements in body and search for parent statements
00063     int childrenSize = 0;
00064     Stmt *parentStmt = NULL;
00065     for ( Stmt::child_iterator i = funcBody->child_begin( ), e = funcBody->child_end( ); i != e; ++i ) {
00066         Stmt *currStmt = *i;
00067         if ( currStmt->getStmtClass( ) != Stmt::ReturnStmtClass ) {
00068             childrenSize++;
00069             if ( currStmt->getStmtClass( ) == Stmt::CallExprClass
00070                  || currStmt->getStmtClass( ) == Stmt::ForStmtClass
00071                  || currStmt->getStmtClass( ) == Stmt::WhileStmtClass
00072                  || currStmt->getStmtClass( ) == Stmt::IfStmtClass
00073                     ) {
00074                 parentStmt = currStmt;
00075             }
00076         }
00077     }
00078 
00079     // if body has multiple children, annotate all statements with performance counter
00080     if ( childrenSize > 1 ) {
00081         bool isCompoundedStatement = false;
00082         SourceLocation lastEventEndLocation;
00083 
00084         for ( Stmt::child_iterator i = funcBody->child_begin( ), e = funcBody->child_end( );
00085               i != e; ++i ) {
00086             Stmt *currStmt = *i;
00087             currStmt->getStmtClassName( );
00088 
00089             if ( currStmt->getStmtClass( ) != Stmt::ReturnStmtClass ) {
00090 
00091                 SourceLocation beginLoc = currStmt->getBeginLoc( );
00092                 SourceLocation endLoc = getLocationAfterToken( currStmt->getEndLoc( ), localContext );
00093 
00094                 // if statement runtime is not merged
00095                 if ( !isCompoundedStatement ) {
00096 
00097                     // set start time always
00098                     rewriter.InsertText( beginLoc,
00099                                          "dataStorage.startEvent(" + std::to_string( internalIdentifier ) + ");\n" );
00100 
00101                     // if statement is not parent, wait for next statement
00102                     if ( currStmt->getStmtClass( ) != Stmt::CallExprClass &&
00103                          currStmt->getStmtClass( ) != Stmt::ForStmtClass &&
00104                          currStmt->getStmtClass( ) != Stmt::WhileStmtClass &&
00105                          currStmt->getStmtClass( ) != Stmt::IfStmtClass
00106                             ) {
00107 
00108                         lastEventEndLocation = endLoc;
00109                         isCompoundedStatement = true;
00110 
00111                         // statement is parent, do not merge runtime
00112                     } else {
00113                         rewriter.InsertText( endLoc,
00114                                              "dataStorage.endEvent(" + std::to_string( internalIdentifier ) + ");\n" );
00115                         // concat stmtclass and stmtid and add to identifierString
00116                         int64_t id = currStmt->getID( *localContext );
00117                         std::string idString =
00118                                 currStmt->getStmtClassName( ) + std::string( " " ) + std::to_string( id );
00119                         identifierString += idString + ",";
00120 
00121                         isCompoundedStatement = false;
00122                         internalIdentifier++;
00123                     }
00124 
00125                     // if waiting for next statement
00126                 } else {
00127 
00128                     // if statement is parent, stop last merge and annotate this statement
00129                     if ( currStmt->getStmtClass( ) == Stmt::CallExprClass ||
00130                          currStmt->getStmtClass( ) == Stmt::ForStmtClass ||
00131                          currStmt->getStmtClass( ) == Stmt::WhileStmtClass ||
00132                          currStmt->getStmtClass( ) == Stmt::IfStmtClass
00133                             ) {
00134 
00135                         // end old event
00136                         rewriter.InsertText( beginLoc,
00137                                              "dataStorage.endEvent(" + std::to_string( internalIdentifier ) +
00138                                              ");\n" );
00139 
00140                         // add compound description to identifier string
00141                         identifierString += "CustomCompoundStmt InterID" + to_string( internalIdentifier++ ) + ",";
00142 
00143                         // start and end new event around parent function
00144                         rewriter.InsertText( beginLoc,
00145                                              "dataStorage.startEvent(" + std::to_string( internalIdentifier ) +
00146                                              ");\n" );
00147                         rewriter.InsertText( endLoc,
00148                                              "dataStorage.endEvent(" + std::to_string( internalIdentifier++ ) +
00149                                              ");\n" );
00150 
00151                         // concat stmtclass and stmtid and add to identifierString
00152                         int64_t id = currStmt->getID( *localContext );
00153                         std::string idString =
00154                                 currStmt->getStmtClassName( ) + std::string( " " ) + std::to_string( id );
00155                         identifierString += idString + ",";
00156 
00157                         isCompoundedStatement = false;
00158 
00159                         // wait for next statement again
00160                     } else {
00161                         lastEventEndLocation = endLoc;
00162                         isCompoundedStatement = true;
00163                     }
00164                 }
00165             }
00166         }
00167 
00168         // end last event
00169         if ( isCompoundedStatement && lastEventEndLocation.isValid( )) {
00170             rewriter.InsertText( lastEventEndLocation,
00171                                  "dataStorage.endEvent(" + std::to_string( internalIdentifier++ ) + ");\n" );
00172             identifierString += "CustomCompoundStmt InterID" + to_string( internalIdentifier ) + ",";
00173         }
00174 
00175         // set transformation successful
00176         transformationSuccess = true;
00177 
00178     }
00179         // if body has zero or one statements
00180     else {
00181         // if one children is parent of another set of statements
00182         // call traverseChildren recursively
00183         if ( parentStmt != NULL ) {
00184             if ( parentStmt->getStmtClass( ) == Stmt::CallExprClass ) {
00185                 CallExpr *expr = cast<CallExpr>( parentStmt );
00186                 Stmt *nextBody = expr->getDirectCallee( )->getBody( );
00187                 traverseChildren( nextBody, localContext );
00188             }
00189             if ( parentStmt->getStmtClass( ) == Stmt::ForStmtClass ) {
00190                 ForStmt *forStmt = cast<ForStmt>( parentStmt );
00191                 Stmt *nextBody = forStmt->getBody( );
00192                 traverseChildren( nextBody, localContext );
00193             }
00194             if ( parentStmt->getStmtClass( ) == Stmt::WhileStmtClass ) {
00195                 WhileStmt *whileStmt = cast<WhileStmt>( parentStmt );
00196                 Stmt *nextBody = whileStmt->getBody( );
00197                 traverseChildren( nextBody, localContext );
00198             }
00199             if ( parentStmt->getStmtClass( ) == Stmt::IfStmtClass ) {
00200                 IfStmt *ifStmt = cast<IfStmt>( parentStmt );
00201                 Stmt *nextBody = ifStmt->getThen( );
00202                 traverseChildren( nextBody, localContext );
00203             }
00204             // if return is the only statement annotate nothing
00205         } else {
00206             // cout << "hagn-tool hasn't found any statements!\n";
00207         }
00208     }
00209 }
