00212 class Visitor : public RecursiveASTVisitor<Visitor> {
00213 private:
00214     ASTContext *astContext; // used for getting additional AST info
00215 
00216 public:
00217     explicit Visitor( ASTContext *Context )
00218             : astContext( Context ) // initialize private members
00219     {
00220         rewriter.setSourceMgr( astContext->getSourceManager( ),
00221                                astContext->getLangOpts( ));
00222     }
00223 
00224     /********************************************/
00232     virtual bool VisitStmt( Stmt *stmt ) {
00233         // only statements in main file
00234         if ( astContext->getSourceManager( ).isInMainFile( stmt->getBeginLoc( ))) {
00235             // when stmt identifier is specified
00236             if ( statementOption != "start" ) {
00237 
00238                 // get statement identifier and cast to string
00239                 int64_t currentId = stmt->getID( *astContext );
00240                 std::string currentIdString = std::to_string( currentId );
00241 
00242                 // find statement with matching id
00243                 if ( currentIdString == statementOption ) {
00244                     rewriter.InsertText( stmt->getBeginLoc(), "dataStorage.startEvent(1);\n" );
00245                     rewriter.InsertText( getLocationAfterToken(stmt->getEndLoc(), astContext), "dataStorage.endEvent(1);\n" );
00246 
00247                     if ( stmt->getStmtClass( ) == Stmt::CallExprClass ) {
00248                         CallExpr *expr = cast<CallExpr>( stmt );
00249                         Stmt *nextBody = expr->getDirectCallee( )->getBody( );
00250                         traverseChildren( nextBody, astContext );
00251                     }
00252                     if ( stmt->getStmtClass( ) == Stmt::ForStmtClass ) {
00253                         ForStmt *forStmt = cast<ForStmt>( stmt );
00254                         Stmt *nextBody = forStmt->getBody( );
00255                         traverseChildren( nextBody, astContext );
00256                     }
00257                     if ( stmt->getStmtClass( ) == Stmt::WhileStmtClass ) {
00258                         WhileStmt *whileStmt = cast<WhileStmt>( stmt );
00259                         Stmt *nextBody = whileStmt->getBody( );
00260                         traverseChildren( nextBody, astContext );
00261                     }
00262                     if ( stmt->getStmtClass( ) == Stmt::IfStmtClass ) {
00263                         IfStmt *ifStmt = cast<IfStmt>( stmt );
00264                         Stmt *nextBody = ifStmt->getThen( );
00265                         traverseChildren( nextBody, astContext );
00266                     }
00267                 }
00268             }
00269         }
00270         return 1;
00271     }
