00281     virtual bool VisitFunctionDecl( FunctionDecl *func ) {
00282 
00283         // find initialization location for includes and initialization of hagn lib
00284         if ( initializationLocation.isInvalid( ) &&
00285              astContext->getSourceManager( ).isInMainFile( func->getLocation( ))) {
00286             initializationLocation = func->getOuterLocStart( );
00287         }
00288 
00289         // find first location in main class for runtime measurement
00290         if ( runtimeStartLocation.isInvalid( ) &&
00291              astContext->getSourceManager( ).isInMainFile( func->getLocation( ))) {
00292             if ( func->isMain( )) {
00293                 SourceLocation locAfterBrace( clang::Lexer::findLocationAfterToken(
00294                         func->getBody( )->getBeginLoc( ).getLocWithOffset( -1 ), clang::tok::l_brace,
00295                         astContext->getSourceManager( ),
00296                         astContext->getLangOpts( ), true ));
00297 
00298                 runtimeStartLocation = locAfterBrace;
00299             }
00300         }
00301 
00302         // find summary location in main class for ending runtime event and print event
00303         if ( summaryLocation.isInvalid( ) &&
00304              astContext->getSourceManager( ).isInMainFile( func->getLocation( ))) {
00305             if ( func->isMain( )) {
00306                 for ( Stmt::child_iterator i = func->getBody( )->child_begin( ), e = func->getBody( )->child_end( );
00307                       i != e; ++i ) {
00308                     Stmt *currStmt = *i;
00309                     if ( currStmt->getStmtClass( ) == Stmt::ReturnStmtClass ) {
00310                         summaryLocation = currStmt->getBeginLoc( );
00311                     }
00312                 }
00313             }
00314         }
00315 
00316         // annotate only main function if statement option is start
00317         if ( astContext->getSourceManager( ).isInMainFile( func->getLocation( ))) {
00318             if ( statementOption == "start" ) {
00319                 if ( func->isMain( )) {
00320                     traverseChildren( func->getBody( ), astContext );
00321                 }
00322             }
00323         }
00324 
00325         return 1;
00326     }
00327 
00328     virtual ~Visitor( ) { }
