00438         identifierString.pop_back( );
00439         if ( initializationLocation.isValid( )) {
00440             rewriter.InsertText( initializationLocation,
00441                                  "#include \"../lib/DataStorage.cpp\"\n""DataStorage dataStorage(\"" +
00442                                  identifierString + "\");" );
00443         } else {
00444             cerr << "Invalid initializationLocation, Line 356";
00445             exit( EXIT_FAILURE );
00446         }
00447 
00448         // start an event at the beginning of the main function
00449         if ( runtimeStartLocation.isValid( )) {
00450             rewriter.InsertText( runtimeStartLocation, "dataStorage.startEvent(0);\n" );
00451         } else {
00452             cerr << "Invalid runtimeStartLocation, Line 364";
00453             exit( EXIT_FAILURE );
