00463 
00464         // runtime end
00465         std::chrono::system_clock::time_point endTime = std::chrono::system_clock::now( );
00466         std::chrono::duration<double, std::milli> ms_double = endTime - startTime;
00467 
00468         // print hagn tool runtime
